definition.script:
pipeline {
    agent any

    parameters {
        choice(
            name: 'ENVIRONMENT',
            choices: ['dev', 'qa', 'prod', 'staging'],
            description: 'Select the target environment'
        )

        choice(
            name: 'DATABASE_TYPE',
            choices: ['mysql', 'postgresql', 'oracle', 'mssql'],
            description: 'Select the database type'
        )

        string(
            name: 'VERSION',
            defaultValue: '1.0',
            description: 'Enter the version number'
        )

        string(
            name: 'DB_USERNAME',
            defaultValue: 'admin',
            description: 'Enter the database username'
        )
    }

    stages {
        stage('Checkout') {
            steps {
                echo 'Checking out code from version control'
                // Add your version control system checkout steps here
            }
        }

        stage('Build') {
            steps {
                echo 'Building the application'
                // Add your build steps here
            }
        }

        stage('Deploy') {
            steps {
                echo "Deploying to ${params.ENVIRONMENT} environment"
                echo "Version: ${params.VERSION}"
                echo "Database Type: ${params.DATABASE_TYPE}"
                echo "Database Username: ${params.DB_USERNAME}"
                // Add your deployment steps here
            }
        }

        stage('Test') {
            steps {
                echo 'Running tests'
                // Add your test steps here
            }
        }

        stage('Cleanup') {
            steps {
                echo 'Performing cleanup tasks'
                // Add your cleanup steps here
            }
        }
    }

    post {
        success {
            echo 'Pipeline executed successfully'
        }
        failure {
            echo 'Pipeline failed'
        }
        always {
            echo 'Pipeline completed'
        }
    }
}
